var $bm_rt;
let sx = effect('X')('Slider');
let sy = effect('Y')('Slider');
let tc = effect('C')('Slider');
let p = effect('P')('Slider');
let ax = $bm_mul($bm_neg(Math.sin($bm_sum(p, 1.57))), sx);
let ay = $bm_mul($bm_neg(Math.cos($bm_sum(p, 1.57))), sy);
let a = [
        ax,
        ay
    ];
let bx = $bm_mul(Math.sin(p), sx);
let by = $bm_mul(Math.cos(p), sy);
let b = [
        bx,
        by + tc
    ];
let cx = $bm_mul($bm_neg(Math.sin($bm_sum(p, 4.71))), sx);
let cy = $bm_mul($bm_neg(Math.cos($bm_sum(p, 4.71))), sy);
let c = [
        cx,
        cy
    ];
let dx = $bm_mul($bm_neg(Math.sin(p)), sx);
let dy = $bm_mul($bm_neg(Math.cos(p)), sy);
let d = [
        dx,
        dy
    ];
$bm_rt = createPath([
    a,
    b,
    c,
    d
], [], [], true);

var $bm_rt;
let sx = effect('X')('Slider');
let sy = effect('Y')('Slider');
let tc = effect('C')('Slider');
let p = effect('P 2')('Slider');
let ax = $bm_mul($bm_neg(Math.sin($bm_sum(p, 1.57))), sx);
let ay = $bm_mul($bm_neg(Math.cos($bm_sum(p, 1.57))), sy);
let h = effect('H')('Slider');
let a = [
        ax,
        ay + h
    ];
let bx = $bm_mul(Math.sin(p), sx);
let by = $bm_mul(Math.cos(p), sy);
let b = [
        bx,
        by + tc + h
    ];
let cx = $bm_mul($bm_neg(Math.sin($bm_sum(p, 4.71))), sx);
let cy = $bm_mul($bm_neg(Math.cos($bm_sum(p, 4.71))), sy);
let c = [
        cx,
        cy + h
    ];
let dx = $bm_mul($bm_neg(Math.sin(p)), sx);
let dy = $bm_mul($bm_neg(Math.cos(p)), sy);
let d = [
        dx,
        dy + h
    ];
$bm_rt = createPath([
    a,
    b,
    c,
    d
], [], [], true);

var $bm_rt;
let topFace = content('top').content('Path 1').path;
let botFace = content('bot').content('Path 1').path;
let h = effect('H')('Slider');
let hand = effect('HANDLES')('Slider');
let swing = linear(hand, 0, 100, 0, h);
let z = [
        0,
        0
    ];
let t = [
        0,
        swing
    ];
let tm = [
        0,
        -swing
    ];
let a = topFace.points()[0];
let b = topFace.points()[1];
let ap = botFace.points()[0];
let bp = botFace.points()[1];
$bm_rt = createPath([
    a,
    ap,
    bp,
    b
], [
    z,
    tm,
    z,
    t
], [
    t,
    z,
    tm,
    z
], true);

